import React from "react";
import { X } from "lucide-react";
import classNames from "classnames/bind";
import styles from "./ProFile.module.scss";

const cx = classNames.bind(styles);
const buckets = "ichigomazone";

const ProFile1 = ({ datax, onClose }) => {
  const selectedUser = {
    name: datax.name || "",
    avatar: `https://${buckets}.s3.amazonaws.com/${datax.avatar}`,
    phone: "**********",
    gender: datax.gender || "",
    birthDate: "**/**/****",
  };
  const selectedGender = datax.gender === "female" ? "Nữ" : "Nam";
  const birthDay = "*";
  const birthMonth = "*";
  const birthYear = "*";

  const handleMessage = () => {
    // Tạm thời để trống vì không yêu cầu hành động cụ thể
